                Text = "Submit",
                Top = 230,
                Left = 150,
                Width = 100,
                BackColor = Color.Aqua,
                ForeColor = Color.White
            };
            btnSubmit.Click += (s, e) => { this.DialogResult = DialogResult.OK; this.Close(); };

